package com.example.instagramclone;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;

import android.app.ProgressDialog;
import android.content.Intent;
import android.os.Bundle;
import android.text.TextUtils;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;

import com.example.instagramclone.Model.User;
import com.google.android.gms.tasks.OnCompleteListener;
import com.google.android.gms.tasks.Task;
import com.google.firebase.auth.AuthResult;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;

import java.util.HashMap;

public class CreateAccount extends AppCompatActivity {
    EditText userIdReg, emailReg, fullNameReg, passReg;
    Button registerBtn;
    FirebaseAuth auth;
    DatabaseReference reference;
    ProgressDialog pd;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_create_account);

        userIdReg = findViewById(R.id.userIdReg);
        fullNameReg = findViewById(R.id.fullNameReg);
        emailReg = findViewById(R.id.emailReg);
        passReg = findViewById(R.id.passReg);
        registerBtn = findViewById(R.id.registerBtn);

        auth =  FirebaseAuth.getInstance();

        registerBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                pd = new ProgressDialog(CreateAccount.this);
                pd.setMessage("Please wait...");
                pd.show();

                String userName = userIdReg.getText().toString();
                String fullName = fullNameReg.getText().toString();
                String email = emailReg.getText().toString();
                String pass = passReg.getText().toString();

                if(TextUtils.isEmpty(userName) || TextUtils.isEmpty(fullName) || TextUtils.isEmpty(email) || TextUtils.isEmpty(pass)){
                    Toast.makeText(CreateAccount.this, "All fields are required!", Toast.LENGTH_SHORT).show();
                }else if(pass.length() < 6){
                    Toast.makeText(CreateAccount.this, "Password must contain more or equal 6 letters!", Toast.LENGTH_SHORT).show();
                }
                else{
                    register(userName, fullName, email, pass);

                }
            }
        });


    }

    private void register (String userName, String fullName, String email, String pass){
        auth.createUserWithEmailAndPassword(email, pass).addOnCompleteListener(CreateAccount.this, new OnCompleteListener<AuthResult>() {
            @Override
            public void onComplete(@NonNull Task<AuthResult> task) {
                if(task.isSuccessful()){
                    FirebaseUser user = auth.getCurrentUser();
                    String userId = user.getUid();
                    User user1 = new User();

                    reference = FirebaseDatabase.getInstance().getReference().child("Users").child(userId);

                    user1.setId(userId);
                    user1.setUserName(userName.toLowerCase());
                    user1.setFullName(fullName);
                    user1.setEmail(email);
                    user1.setImageUrl("data:image/png;base64,");

//                    HashMap<String, Object> hashMap = new HashMap<>();
//                    hashMap.put("Id", userId);
//                    hashMap.put("UserName", userName.toLowerCase());
//                    hashMap.put("FullName", fullName);
//                    hashMap.put("Email", email);

                    reference.setValue(user1).addOnCompleteListener(new OnCompleteListener<Void>() {
                        @Override
                        public void onComplete(@NonNull Task<Void> task) {
                            if (task.isSuccessful()){
                                pd.dismiss();
                                startActivity(new Intent(CreateAccount.this, HomeActivity.class));
                                finish();
                            }
                        }
                    });
                }else{
                    pd.dismiss();
                    Toast.makeText(CreateAccount.this, "You already have an account with this Email, Please Log In", Toast.LENGTH_SHORT).show();
                    startActivity(new Intent(CreateAccount.this, StartActivity.class));
                }
            }
        });
    }

}